import React, {useState, useEffect} from 'react';
import {useHistory} from 'react-router-dom';
import axios from 'axios';
import Link from 'react-router-dom';
import ConfirmDeletion from './Components/ConfirmDeletion';

const ListBodyTypes = () => {
  const [bodyTypes, setBodyTypes] = useState([]);
  const [selectedBodyTypeId, setSelectedBodyTypeId] = useState(null);
  const [isModalOpen, setIsModalOpen] = useState(false);
  const [isPopupOpen, setIsPopupOpen] = useState(false);
  const [popupMessage, setPopupMessage] = useState('');
  const [popupType, setPopupType] = useState('info'); // 'info' or 'error'

  useEffect(() => {
    const fetchBodyTypes = async () => {
      try {
        const response = await fetch('http://localhost:3050/api/bodyTypes');
        const data = await response.json();
        setBodyTypes(data);
      } catch (error) {
        console.error('Error fetching body types:', error);
      }
    };

    fetchBodyTypes();
  }, []);



const handleDeleteClick = (bodyTypeId) => {
  setSelectedBodyTypeId(bodyTypeId); // Set the option ID to state
  setIsModalOpen(true); // Show the modal
};

const confirmDelete = async () => {
  if (selectedBodyTypeId) {
    try {
      await axios.delete(`http://localhost:3050/api/bodyTypes/deleteBodyType/${selectedBodyTypeId}`);
      setIsModalOpen(false); // Close the modal
      // Custom success notification
      setPopupMessage('Body Type Deleted Successfully!');
      setPopupType('info');
      setIsPopupOpen(true);
      setBodyTypes(bodyTypes.filter(bodyType => bodyType._id !== selectedBodyTypeId)); // Update state to remove the item
    } catch (error) {
      console.error('Error deleting body type:', error.message);
      // Custom error notification
      setPopupMessage('Error deleting body type. Please try again.');
      setPopupType('error');
      setIsPopupOpen(true);
    }
  }
};

return ( 
  <div>
    <h1>Body Types</h1>
    <table>
      <thead>
        <tr>
          <th>Name</th>
          <th>Description</th>
          <th>Actions</th>
        </tr>
      </thead>
      <tbody>
        {bodyTypes.map((bodyType) => (
          <tr key={bodyType._id}>
            <td>{bodyType.name}</td>
            <td>{bodyType.description}</td>
            <td>
              <button onClick={() => handleDeleteClick(bodyType._id)}>Delete</button>
            </td>
          </tr>
        ))}
      </tbody>
    </table>
    {isModalOpen && (
      <ConfirmDeletion
        onConfirm={confirmDelete}
        onCancel={() => setIsModalOpen(false)}
      />
    )}
    {isPopupOpen && (
      <div className={`popup ${popupType}`}>
        <p>{popupMessage}</p>
        <button onClick={() => setIsPopupOpen(false)}>Close</button>
      </div>
    )}
  </div>
);
}

export default ListBodyTypes;
