import React, { useState, useEffect, useRef } from "react";
import axios from "axios";
import { useParams } from "react-router-dom";
import {useNavigate} from 'react-router-dom'    //for programmatic navigation.
import { Link } from "react-router-dom"; // Import Link from react-router-dom
// import html2canvas from 'html2canvas';
// import jsPDF from 'jspdf';
import bgblue from '../../../src/assets/recommendations/bgblue1.jpg'; // Background image
import ConfirmDeletion from '../Recommendations/Components/ConfirmDeletion'; // Import the modal component
import CustomPopup from '../Recommendations/Components/CustomPopup'; // Import the modal component


const TestPage = () => {
    const pdfRef = useRef();

    const [selectedMeasurements, setSelectedMeasurements] = useState(null);
    const [measurements, setMeasurements] = useState([]);
    const [selectedMeasurementsId, setSelectedMeasurementsId] = useState(null);
    const { measurementsId } = useParams(); // Get the measurementsId from URL params



    const [isModalOpen, setIsModalOpen] = useState(false);
    const [selectedEventId, setSelectedEventId] = useState(null);
 
    const [isPopupOpen, setIsPopupOpen] = useState(false);
    const [popupMessage, setPopupMessage] = useState('');
    const [popupType, setPopupType] = useState('info'); // 'info' or 'error'

    const [isDeleting, setIsDeleting] = useState(false);



//     const downloadPDF = () => {
//     const input = pdfRef.current;
//     html2canvas(input, {
//         scale: 2, // Increasing the scale factor to enhance quality
//         useCORS: true, // Ensures that images hosted on different origins can be loaded correctly
//     }).then((canvas) => {
//         const imgData = canvas.toDataURL('image/png');      
//         const pdf = new jsPDF('p', 'mm', 'a4');        
//         const pdfWidth = pdf.internal.pageSize.getWidth();       
//         const pdfHeight = pdf.internal.pageSize.getHeight();        
//         const imgWidth = canvas.width / 2; // Adjust the width to account for the scale
//         const imgHeight = canvas.height / 2; // Adjust the height to account for the scale
//         const ratio = Math.min(pdfWidth / imgWidth, pdfHeight / imgHeight);
//         const imgX = (pdfWidth - imgWidth * ratio) / 2; // Center the image
//         const imgY = 10;
//         pdf.addImage(imgData, 'PNG', imgX, imgY, imgWidth * ratio, imgHeight * ratio);
//         pdf.save('eventDetails.pdf');
//     });
// };

 
  const navigate = useNavigate();

  
    

     
  useEffect(() => {
    // Fetch measurements data based on measurementsId when the component mounts
    async function getMyMeasurements() {
      try {
        const response = await axios.get(`http://localhost:3050/measurement/getUserMeasurements/${measurementsId}`);
        setSelectedMeasurements(response.data.userMeasurements);
        console.log("Fetched Measurements Details Successfully");
       
      } catch (error) {
        console.error("Error fetching Measurements data:", error.message);
        alert("Error fetching Measurements data. Please try again.");
      }
    }

    getMyMeasurements();
  }, [measurementsId]);


  useEffect(() => {
    console.log("Measurements ID:", measurementsId);
  }, [measurementsId]);
  
  

  const confirmDelete = async () => {
    if (selectedEventId) {
      try {
        await axios.delete(`http://localhost:305/measurement/deleteMyMeasurements/${selectedMeasurementsId}`);
        setIsModalOpen(false); // Close the modal
        // Custom success notification
        setPopupMessage("Event Deleted Successfully!");
        setPopupType('info');
        setIsPopupOpen(true);


      } catch (error) {
        console.error("Error deleting event:", error.message);
        // Custom success notification
        setPopupMessage("Error deleting event. Please try again!");
        setPopupType('error');
        setIsPopupOpen(true);
      }
    }
  };
  

  return (
    <div className="relative min-h-screen">
    {/* Background Image */}
    <div
      className="absolute inset-0 z-0 bg-fixed"
      style={{
        backgroundImage: `url(${bgblue})`,
        backgroundSize: 'cover',
        backgroundPosition: 'center',
      }}
      
    >
  </div>

     
    
    {/* Content Wrapper */}
    <div className="relative z-10 flex flex-col items-center justify-center min-h-screen">
s
  
      {/* Your scrolling content */}
      
      <div className="container max-w-5xl p-5 mx-auto my-10 overflow-auto bg-fixed bg-opacity-50 border-8 border-double shadow-2xl rounded-3xl bg-gray-50 shadow-theme-green border-theme-green" >

        <div ref={pdfRef}>

      
  
        <div className="grid grid-cols-1 gap-10 pt-4 lg:px-40 sm:px-10 sm:grid-cols-1 lg:grid-cols-1 xl:grid-cols-1" >
            {/* Event Details */}
            <div className="container flex items-center justify-center w-full overflow-hidden shadow-md rounded-3xl h-96">
                <img className="object-fill w-full h-full" src={``} 
                />
            </div>
        </div>

            <div className="grid grid-cols-1 gap-10 pt-4 lg:px-40 sm:px-10 sm:grid-cols-1 lg:grid-cols-1 xl:grid-cols-1" >

            <div >
                {/* Event Name with Inika font */}
                <h1 className="text-4xl font-bold text-center text-green-800 font-inika">Your Body Type: Hourglass</h1>
                
                <div className="flex justify-between px-16 mt-1">
                {/* Event Date with Lexend font */}
                <h6 className="text-base text-center text-gray-600 font-lexend ">Ella Jungle Resort</h6>
                

                </div>
                
                

    
                {/* Event Description with McLaren font */}
                <div className="mt-2 p-des max-h-24">
                <p className="text-lg text-center font-mclaren">Description</p>

                </div>
            </div>



            
            





         

            {/* Empty div */}
            <div></div>

        </div>

        
    </div>





  	<div className="mb-2">

   


                <div className="flex items-center justify-between mx-48 mt-8 mb-8">
                    {/* Using Link component for View button */}
                    <Link to={`/updateMyMeasurements/${selectedMeasurements._id}`} className="flex items-center text-white text-xl font-mclaren px-3 py-1  bg-blue-500 hover:bg-blue-800   rounded-3xl">
                <svg xmlns="http://www.w3.org/2000/svg" fill="none" viewBox="0 0 24 24" strokeWidth={1.5} stroke="currentColor" className="w-6 h-6 mr-1">
                <path strokeLinecap="round" strokeLinejoin="round" d="M16.023 9.348h4.992v-.001M2.985 19.644v-4.992m0 0h4.992m-4.993 0 3.181 3.183a8.25 8.25 0 0 0 13.803-3.7M4.031 9.865a8.25 8.25 0 0 1 13.803-3.7l3.181 3.182m0-4.991v4.99" />
              </svg> Edit </Link>

                    {/* <button className="flex items-center px-2 py-1 text-xl text-white bg-black font-lexend hover:bg-gray-800 rounded-xl" 
                    onClick={downloadPDF}>
                      <svg xmlns="http://www.w3.org/2000/svg" fill="none" viewBox="0 0 24 24" strokeWidth={1.5} stroke="currentColor" className="w-6 h-6 mr-1">
  <path strokeLinecap="round" strokeLinejoin="round" d="M3 16.5v2.25A2.25 2.25 0 0 0 5.25 21h13.5A2.25 2.25 0 0 0 21 18.75V16.5M16.5 12 12 16.5m0 0L7.5 12m4.5 4.5V3" />
</svg>

                        Download Report
                    </button> */}

                    
<button className="ml-5 flex items-center text-white text-xl font-mclaren px-3 py-1  bg-red-500 hover:bg-red-800 rounded-3xl" 
               onClick={() => {
                setSelectedMeasurementsId(selectedMeasurements._id);
                setIsModalOpen(true);
              }}
              >
                <svg xmlns="http://www.w3.org/2000/svg" fill="none" viewBox="0 0 24 24" strokeWidth={1.5} stroke="currentColor" className="w-6 h-6 mr-1">
              <path strokeLinecap="round" strokeLinejoin="round" d="m14.74 9-.346 9m-4.788 0L9.26 9m9.968-3.21c.342.052.682.107 1.022.166m-1.022-.165L18.16 19.673a2.25 2.25 0 0 1-2.244 2.077H8.084a2.25 2.25 0 0 1-2.244-2.077L4.772 5.79m14.456 0a48.108 48.108 0 0 0-3.478-.397m-12 .562c.34-.059.68-.114 1.022-.165m0 0a48.11 48.11 0 0 1 3.478-.397m7.5 0v-.916c0-1.18-.91-2.164-2.09-2.201a51.964 51.964 0 0 0-3.32 0c-1.18.037-2.09 1.022-2.09 2.201v.916m7.5 0a48.667 48.667 0 0 0-7.5 0" />
            </svg> Reset </button>

                </div>  
                

                
        </div>  

       
    </div>
    {/* Scrolling content End*/}

        
      {/* Use the Confirm Deletion Modal here */}
      <ConfirmDeletion
        isOpen={isModalOpen}
        onClose={() => setIsModalOpen(false)}
        onConfirm={confirmDelete}
      />

      {/* Your component structure */}
      <CustomPopup
        isOpen={isPopupOpen}
        message={popupMessage}
        onClose={() => {
          setIsPopupOpen(false);
          setIsModalOpen(false); // Close the modal
          if (selectedEvent.isPublic) {
            navigate("/events");
          } else {
            navigate("/myEvents");
          }
        }}
        type={popupType}
      />
      
    
    </div>
  </div>
  )
}

export default TestPage
